sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (!newValue == '') {
        g_form.setSectionDisplay('device_details', true);
        g_form.setSectionDisplay('delievery_details', true);

        g_form.getReference('u_reference_1', getDetails);

    }

}

function getDetails(details) {
    g_form.setValue('u_string_4', details.model_number);
    g_form.setValue('u_price_6', details.u_price_2);
    g_form.setValue('u_string_5', details.short_description);
    g_form.setValue('u_glide_date_7', details.warranty_expiration);


}
